				strArExtensions[1] = "*.csv";
				strArExtensions[4] = "alle Dateien - *.*";
				strArExtensions[5] = "*.*";
				xOK = Sal.DlgOpenFile(this, "Beihilfen einlesen", strArExtensions, 4, ref nIndex, ref strDateiName, ref strDateiPfad);
				if (xOK) 
				{
