		public SalNumber PalHoleDaten(SalBoolean bMich)
		{
			#region Local Variables
			SqlLocals.PalHoleDatenLocals locals = new SqlLocals.PalHoleDatenLocals();
			#endregion
			
			#region Actions
			using (new SalContext(this, locals))
			{

				// PPJ: Assign parameters to the locals instance.
				locals.bMich = bMich;

				strSelect = @"SELECT eezei, eearztnr, eehalternr, eedatum, eeerldatum, ROWID FROM ee
INTO :tblEE.colsVon, :tblEE.colnArztNr, :tblEE.colnHalterNr, :tblEE.coldtDatum, :tblEE.coldtErlDatum, :tblEE.colsROWID
WHERE eeerldatum IS NULL";
				if (locals.bMich) 
				{
					strSelect = strSelect + " AND eezei = :strBNZei";
				}
				strSelect = strSelect + " ORDER BY eedatum, eearztnr, eehalternr";
				tblEE.Populate(hSqlEL, strSelect, Sys.TBL_FillAll);

				return 0;
			}
			#endregion
		}
