		/// <summary>
		/// SAM_FetchRowDone event handler.
		/// </summary>
		/// <param name="sender"></param>
		/// <param name="e"></param>
		private void tblEE_OnSAM_FetchRowDone(object sender, WindowActionsEventArgs e)
		{
			#region Actions
			e.Handled = true;
			if (this.tblEE.coldtErlDatum.DateTime == Sys.DATETIME_Null) 
			{
				this.tblEE.colnErl.Number = 0;
			}
			else
			{
				this.tblEE.colnErl.Number = 1;
			}
			Int.SqlExecut(this.hSqlELUpd, @"SELECT hvn, hnn, hort INTO :frmEE.strVN, :frmEE.strNN, :frmEE.strOrt FROM h WHERE harztnr = :frmEE.tblEE.colnArztNr AND hhalternr = :frmEE.tblEE.colnHalterNr
 AND hdeaktiv IS NULL");
			this.nFetchEL = this.hSqlELUpd.FetchNext();
			this.tblEE.colsHalter.Text = this.strNN;
			if (this.strVN != "") 
			{
				this.tblEE.colsHalter.Text = this.tblEE.colsHalter.Text + ", " + this.strVN;
			}
			if (this.strOrt != "") 
			{
				this.tblEE.colsHalter.Text = this.tblEE.colsHalter.Text + ", " + this.strOrt;
			}
			#endregion
		}
		
