		private void pbAdd_OnSAM_Click(object sender, WindowActionsEventArgs e)
		{
			#region Actions
			e.Handled = true;
			if (this.dfDatum.DateTime == Sys.DATETIME_Null) 
			{
				Sal.MessageBox("Das Datum ist ungültig. Bitte geben Sie ein gültiges Datum ein.", "Datum ungültig", (Sys.MB_Ok | Sys.MB_IconExclamation));
			}
			else
			{
				Int.SqlIstDa("FROM fe WHERE fedatum = :frmFeiertage.dfDatum", ref this.bDa);
				if (this.bDa) 
				{
					Sal.MessageBox("Das Datum war bereits gespeichert.", "Datum bereits vorhanden", (Sys.MB_Ok | Sys.MB_IconExclamation));
				}
				else
				{
					Int.SqlImmed("INSERT INTO fe (fedatum) VALUES( :frmFeiertage.dfDatum)");
					this.PalPopulate();
				}
			}
			#endregion
		}
		
