			// ----------------------------------------------LÖSCHEN-------------------------------------------------------------------------------
			if (this.tblFeiertage.AnyRows(Sys.ROW_Selected, 0)) 
			{
				Int.SqlConnection(ref this.hSqlFe);
				this.hSqlFe.Prepare(@"DELETE FROM fe
WHERE fedatum = :frmFeiertage.tblFeiertage.colDatum ");
				if (this.tblFeiertage.DoDeletes(this.hSqlFe, Sys.ROW_Selected)) 
				{
					this.hSqlFe.Disconnect();
					this.PalPopulate();
				}
				else
				{
					this.hSqlFe.Disconnect();
				}
			}
			#endregion
		}
