		/// <summary>
		/// SAM_Click event handler.
		/// </summary>
		/// <param name="sender"></param>
		/// <param name="e"></param>
		private void pbOk_OnSAM_Click(object sender, WindowActionsEventArgs e)
		{
			#region Actions
			e.Handled = true;
			if (this.dfvon.IsEmpty()) 
			{
				this.dfvon.Number = 1;
			}
			if (this.dfbis.IsEmpty()) 
			{
				this.dfbis.Number = this.dfvon.Number;
			}
			if (Sal.IsNull(this.dfDvon)) 
			{
				this.dfDvon.Value = new SalDateTime(1995, 1, 1);
			}
			if (Sal.IsNull(this.dfDbis)) 
			{
				this.dfDbis = this.dfDvon;
				if (this.dfDvon.Value == new SalDateTime(1995, 1, 1)) 
				{
					this.dfDbis.Value = new SalDateTime(2100, 12, 31);
				}
			}
			Sal.WaitCursor(true);
			this.tblZahl.Populate(this.hSqlZahl, @"SELECT @MONTHBEG(zabuchdat),
SUM( @CHOOSE(zakz, zadm, zadm, zadm, zadm, 0,0,0) ),
SUM( @CHOOSE(zakz, 0,0,0,0, zadm, zadm) )
INTO :frmInfoZahlAusbuch.tblZahl.coldtDatum,
:frmInfoZahlAusbuch.tblZahl.colnZahl,
:frmInfoZahlAusbuch.tblZahl.colnAusbuch
FROM za WHERE zaarztnr BETWEEN :frmInfoZahlAusbuch.dfvon AND :frmInfoZahlAusbuch.dfbis
AND zabuchdat BETWEEN :frmInfoZahlAusbuch.dfDvon.Value AND :frmInfoZahlAusbuch.dfDbis.Value AND zadeaktiv IS NULL
GROUP BY 1 ORDER BY 1", Sys.TBL_FillAll);
			this.hSqlZahl.Commit();
            //FC:FINAL: replace cQuickGraph
            this.ccZahl.Draw(null);
			Sal.WaitCursor(false);
			#endregion
		}
		
