:frmInventHaendler.tblIVk.colTelBest, :frmInventHaendler.tblIVk.colTelSupp, :frmInventHaendler.tblIVk.colEMail, :frmInventHaendler.tblIVk.colWertung, :frmInventHaendler.tblIVk.colAnsprech
