		public SalNumber PalNewRow()
		{
			#region Actions
			using (new SalContext(this))
			{
				if (PalSave()) 
				{
				}
				PalHoleTabelle();
				nRow = tblKonti.InsertRow(Sys.TBL_MaxRow);
				tblKonti.colBilanzierer.Number = 0;
				tblKonti.colSKR.Text = "SKR03";
				tblKonti.SetFocusCell(nRow, tblKonti.colCode, 0, -1);
			}

			return 0;
			#endregion
		}
		
		/// <summary>
		/// </summary>
		/// <returns></returns>
		public SalNumber PalSave()
		{
			#region Actions
			using (new SalContext(this))
			{
				Sal.WaitCursor(true);
				tblKonti.KillCellEdit();
				if (tblKonti.AnyRows((Sys.ROW_New | Sys.ROW_Edited), 0)) 
				{
					nRow = Sys.TBL_MinRow;
					while (true)
					{
						if (!(tblKonti.FindNextRow(ref nRow, (Sys.ROW_New | Sys.ROW_Edited), 0))) 
						{
							break;
						}
						tblKonti.SetFocusRow(nRow);
						if (tblKonti.colBilanzierer.Number == Sys.NUMBER_Null) 
						{
							tblKonti.colBilanzierer.Number = 0;
						}


						if (tblKonti.colCode.Text == "") 
						{
							Sal.MessageBox("Der Code darf nicht leer sein.", "Fehler", (Sys.MB_Ok | Sys.MB_IconStop));
						}
						else if (tblKonti.colSKR.Text == "") 
						{
							Sal.MessageBox("Der Kontenrahmen darf nicht leer sein.", "Fehler", (Sys.MB_Ok | Sys.MB_IconStop));
						}
						else
						{
							Int.SqlIstDa("FROM ko WHERE kocode = :frmKontierungen.tblKonti.colCode AND kobilanzierer = :frmKontierungen.tblKonti.colBilanzierer AND kokontenrahmen = :frmKontierungen.tblKonti.colSKR", ref Var.bExists);
							if (Var.bExists) 
							{
								Int.SqlImmed(@"UPDATE ko SET
kosoll = :frmKontierungen.tblKonti.colSoll,
kohaben = :frmKontierungen.tblKonti.colHaben
WHERE kocode = :frmKontierungen.tblKonti.colCode AND kobilanzierer = :frmKontierungen.tblKonti.colBilanzierer AND kokontenrahmen = :frmKontierungen.tblKonti.colSKR");
							}
							else
							{
								Int.SqlImmed(@"INSERT INTO ko
(kocode, kobilanzierer, kokontenrahmen, kosoll, kohaben)
VALUES( :frmKontierungen.tblKonti.colCode, :frmKontierungen.tblKonti.colBilanzierer, :frmKontierungen.tblKonti.colSKR, :frmKontierungen.tblKonti.colSoll, :frmKontierungen.tblKonti.colHaben)");
							}
							tblKonti.SetRowFlags(nRow, (Sys.ROW_New | Sys.ROW_Edited), false);
						}
					}
				}
				Sal.WaitCursor(false);
			}

			return 0;
			#endregion
		}
		
		/// <summary>
		/// </summary>
		/// <returns></returns>
		public SalNumber PalHoleTabelle()
		{
			#region Local Variables
			SqlLocals.PalHoleTabelleLocals locals = new SqlLocals.PalHoleTabelleLocals();
			#endregion
			
			#region Actions
			using (new SalContext(this, locals))
			{
				Int.SqlConnection(ref hSqlUser);
				strSelect = @"SELECT kocode, kobilanzierer, kokontenrahmen, kosoll, kohaben
INTO :frmKontierungen.tblKonti.colCode, :frmKontierungen.tblKonti.colBilanzierer, :frmKontierungen.tblKonti.colSKR, :frmKontierungen.tblKonti.colSoll, :frmKontierungen.tblKonti.colHaben
FROM ko ORDER BY kocode, kokontenrahmen, kobilanzierer";
				if (tblKonti.Populate(hSqlUser, strSelect, Sys.TBL_FillAll)) 
				{
					hSqlUser.Commit();
				}
				hSqlUser.Disconnect();

				return 0;
			}
			#endregion
		}
