		/// <summary>
		/// SAM_Create event handler.
		/// </summary>
		/// <param name="sender"></param>
		/// <param name="e"></param>
		private void tblKonti_OnSAM_Create(object sender, WindowActionsEventArgs e)
		{
			#region Actions
			e.Handled = true;
			Int.SqlConnection(ref this.hSqlUser);
			this.tblKonti.colSKR.ClearList();
			this.hSqlUser.PrepareAndExecute("SELECT dscode INTO :frmKontierungen.strSKR FROM ds ORDER BY dscode");
			this.nFetchUser = this.hSqlUser.FetchNext();
			while (this.nFetchUser != Sys.FETCH_EOF) 
			{
				this.tblKonti.colSKR.AddListItem(this.strSKR);
				this.nFetchUser = this.hSqlUser.FetchNext();
			}
			this.hSqlUser.Commit();
			this.hSqlUser.Disconnect();
			#endregion
		}
		
