		/// <summary>
		/// SAM_Click event handler.
		/// </summary>
		/// <param name="sender"></param>
		/// <param name="e"></param>
		private void pbLoe_OnSAM_Click(object sender, WindowActionsEventArgs e)
		{
			#region Actions
			e.Handled = true;
			if (this.tblKonti.AnyRows((Sys.ROW_Selected | Sys.ROW_MarkDeleted), 0)) 
			{
				this.nRow = Sys.TBL_MinRow;
				while (true)
				{
					if (!(this.tblKonti.FindNextRow(ref this.nRow, (Sys.ROW_Selected | Sys.ROW_MarkDeleted), 0))) 
					{
						break;
					}
					this.tblKonti.SetFocusRow(this.nRow);
					Int.SqlImmed("DELETE FROM ko WHERE kocode = :frmKontierungen.tblKonti.colCode AND kobilanzierer = :frmKontierungen.tblKonti.colBilanzierer AND kokontenrahmen = :frmKontierungen.tblKonti.colSKR");
					this.tblKonti.DeleteRow(this.nRow, Sys.TBL_NoAdjust);
				}
				this.PalSave();
				this.tblKonti.ResetTable();
				// If SalTblPopulate( tblBEZS,hSqlUser,strSelect,TBL_FillAll )
				this.PalHoleTabelle();
			}
			else
			{
				Sal.MessageBox(@"Vorher müssen die zu löschenden
Zeilen markiert werden !", "Markieren !", Sys.MB_IconAsterisk);
			}
			#endregion
		}
		
