		private void frmMahnVor_OnSAM_Create(object sender, WindowActionsEventArgs e)
		{
			#region Actions
			e.Handled = true;
			Int.SqlConnection(ref this.hSqlMahnVor);
			Int.SqlConnection(ref this.hSqlMahnVorDel);
			this.strSelect = @"SELECT mvarztnr, mvhalternr, mvname, mvrechnr, mvrechdat, mvms, mvdmrest, ROWID
INTO :frmMahnVor.tblMahnVor.colnArztNr, :frmMahnVor.tblMahnVor.colnHalterNr,
:frmMahnVor.tblMahnVor.colsName,
:frmMahnVor.tblMahnVor.colnRechNr, :frmMahnVor.tblMahnVor.coldtRechDat,
:frmMahnVor.tblMahnVor.colnMS, :frmMahnVor.tblMahnVor.colnDMRest,
:frmMahnVor.tblMahnVor.colsROWID
FROM MV ORDER BY mvarztnr, mvhalternr";
			this.tblMahnVor.Populate(this.hSqlMahnVor, this.strSelect, Sys.TBL_FillAll);
			this.tblMahnVor.SetFocusCell(0, this.tblMahnVor.colnArztNr, 0, -1);
			Sal.WaitCursor(false);
			#endregion
		}
		
