			this.hWndContainer = Ext.CreateWindowExA(0, "AtlAxWin", this.sBuffer, (Const.WS_CHILD | Const.WS_VISIBLE), 0, 0, this.nRight - this.nLeft, this.nBottom - this.nTop, this, 0, 0, 0);
