		public SalNumber BerechneBrutto(SalNumber nPArzt, SalNumber nPHalter, SalNumber nPRech, SalString strPLA)
		{
			#region Local Variables
			SqlLocals.BerechneBruttoLocals locals = new SqlLocals.BerechneBruttoLocals();
