				locals.nPRech = nPRech;
				locals.strPLA = strPLA;

				locals.nPBrutto = 0;
				Int.SqlConnection(ref locals.hSqlBrutto);

				if (locals.strPLA == "L") 
				{
					Int.SqlHandleExec(locals.hSqlBrutto, @"SELECT rpla, rpmwst, sum(rpdm)
