			#region Actions
			e.Handled = true;
			Int.SqlConnection(ref this.hSqlStatAbr);
			this.tblAbr.ResetTable();
			if (this.tblAbr.Populate(this.hSqlStatAbr, @"SELECT akzeichen, SUM(akanzahl), SUM(akdm), SUM(akrechsumm)
INTO :frmStatAbrg.tblAbr.colBenutzer, :frmStatAbrg.tblAbr.colAnzahl, :frmStatAbrg.tblAbr.colGeb, :frmStatAbrg.tblAbr.colRechSum
FROM ak WHERE aktyp = 'RG' AND aktypdetail = 'UebOP' AND akdeaktiv IS NULL AND akdatum BETWEEN :frmStatAbrg.dfvon AND :frmStatAbrg.dfbis
GROUP BY 1", Sys.TBL_FillAll)) 
			{
			}
			this.hSqlStatAbr.Disconnect();
			#endregion
		}
		
