			#region Actions
			e.Handled = true;
			this.strSelect = @"SELECT @MONTHBEG(tkabeldat),@MONTH(tkabeldat), sum(@choose(@int(tkadmabr),0, tkadmabr)),
sum(@choose(@int(tkadmabr), tkadmabr,0)), sum(tksoll), sum(tkhaben)
INTO :frmStatTK.tblTK.coldtDatum, :frmStatTK.tblTK.colnMonat,
:frmStatTK.tblTK.colnRech,  :frmStatTK.tblTK.colnAusbuch,
:frmStatTK.tblTK.colnSoll, :frmStatTK.tblTK.colnHaben
FROM tka WHERE tkadeaktiv IS NULL ";
			if (!(this.dfArztNr.IsEmpty())) 
			{
				this.strSelect = this.strSelect + "AND tkaarztnr = :frmStatTK.dfArztNr ";
			}
			this.strSelect = this.strSelect + "GROUP BY 1,2 ORDER BY 1,2";

			Sal.WaitCursor(true);
			this.tblTK.Populate(this.hSqlTK, this.strSelect, Sys.TBL_FillAll);
            //FC:FINAL: replace cQuickGraph
            this.cc1.Draw(null);
			Sal.WaitCursor(false);
			#endregion
		}
		
