			#region Actions
			using (new SalContext(this))
			{
				if (PalSave()) 
				{
				}
				tblVA.Populate(hSqlUser, strSelect, Sys.TBL_FillAll);
				nRow = tblVA.InsertRow(Sys.TBL_MaxRow);
				tblVA.colN2.Text = "Veterinäramt";
				tblVA.SetFocusCell(nRow, tblVA.colCode, 0, -1);
