		/// <summary>
		/// </summary>
		/// <returns></returns>
		public SalNumber PalSave()
		{
			#region Actions
			using (new SalContext(this))
			{
				Sal.WaitCursor(true);
				// Call SqlConnection( hSqlLogBugUpd)
				tblVA.KillCellEdit();
				if (tblVA.AnyRows((Sys.ROW_New | Sys.ROW_Edited), 0)) 
				{
					nRow = Sys.TBL_MinRow;
					while (true)
					{
						if (!(tblVA.FindNextRow(ref nRow, (Sys.ROW_New | Sys.ROW_Edited), 0))) 
						{
							break;
						}
						tblVA.SetFocusRow(nRow);

						if (this.tblVA.colCode.Text != "") 
						{
							Int.SqlIstDa("FROM va WHERE vacode = :frmVETAemter.tblVA.colCode", ref Var.bExists);
							if (Var.bExists) 
							{
								if (Sys.IDYES == Sal.MessageBox("Es existiert bereits ein Kreis mit diesem Code. Soll der überschrieben werden?", "Achtung", (Sys.MB_YesNo | Sys.MB_IconQuestion))) 
								{
									Int.SqlImmed("DELETE FROM va WHERE vacode = :frmVETAemter.tblVA.colCode");
									Var.bExists = false;
								}
							}
							if (!(Var.bExists)) 
							{
								Int.SqlImmed(@"INSERT INTO va
( vacode, vaname1, vaname2, vastr, vaort )
VALUES(:frmVETAemter.tblVA.colCode, :frmVETAemter.tblVA.colN1,  :frmVETAemter.tblVA.colN2,  :frmVETAemter.tblVA.colS,  :frmVETAemter.tblVA.colO )");
							}
						}
						tblVA.SetRowFlags(nRow, (Sys.ROW_New | Sys.ROW_Edited), false);
					}
				}
				Sal.WaitCursor(false);
			}

			return 0;
			#endregion
		}
		#endregion
		
