		private void frmVETAemter_OnSAM_Create(object sender, WindowActionsEventArgs e)
		{
			#region Actions
			e.Handled = true;
			Sal.WaitCursor(true);
			this.strSelect = @"SELECT vacode, kreis, vaname1, vaname2, vastr, vaort
INTO :frmVETAemter.tblVA.colCode, :frmVETAemter.tblVA.colKreis, :frmVETAemter.tblVA.colN1, :frmVETAemter.tblVA.colN2, :frmVETAemter.tblVA.colS, :frmVETAemter.tblVA.colO
FROM va, mckreis WHERE vacode=id ORDER BY vacode";
			Int.SqlConnection(ref this.hSqlUser);
			this.tblVA.Populate(this.hSqlUser, this.strSelect, Sys.TBL_FillAll);

			this.tblVA.SetContextMenu("frmVETAemterRightClick", 0);

			Sal.WaitCursor(false);
			#endregion
		}
		
