		/// <summary>
		/// </summary>
		/// <returns></returns>
		public SalNumber PalSave()
		{
			#region Actions
			using (new SalContext(this))
			{
				Sal.WaitCursor(true);
				// Call SqlConnection( hSqlLogBugUpd)
				tblVe.KillCellEdit();
				if (tblVe.AnyRows((Sys.ROW_New | Sys.ROW_Edited), 0)) 
				{
					nRow = Sys.TBL_MinRow;
					while (true)
					{
						if (!(tblVe.FindNextRow(ref nRow, (Sys.ROW_New | Sys.ROW_Edited), 0))) 
						{
							break;
						}
						tblVe.SetFocusRow(nRow);

						if (this.tblVe.colCode.Text != "") 
						{
							Int.SqlIstDa("FROM ve WHERE vecode = :frmVersicherungen.tblVe.colCode", ref Var.bExists);
							if (Var.bExists) 
							{
								Int.SqlImmed("DELETE FROM ve WHERE vecode = :frmVersicherungen.tblVe.colCode");
								Var.bExists = false;
							}
							tblVe.colLand.Text = Sal.StrLeftX(tblVe.colLand.Text, 2);
							if (!(Var.bExists)) 
							{
								Int.SqlImmed(@"INSERT INTO ve
( vecode, vename1, vename2, vestrasse, veort, veland, veemail )
VALUES(:frmVersicherungen.tblVe.colCode,
:frmVersicherungen.tblVe.colN1, :frmVersicherungen.tblVe.colN2, :frmVersicherungen.tblVe.colS, :frmVersicherungen.tblVe.colO, :frmVersicherungen.tblVe.colLand,
:frmVersicherungen.tblVe.colEMail  )");
							}
						}
						tblVe.SetRowFlags(nRow, (Sys.ROW_New | Sys.ROW_Edited), false);
					}
					// 07.08.23 Ä2107 + versicherung
					Sal.ListPopulate(App.frmRgSuchen.cmbVECode, Var.hSql, "SELECT vecode FROM ve order by vecode");
					Sal.ListAdd(App.frmRgSuchen.cmbVECode, "");
				}
				Sal.WaitCursor(false);
			}

			return 0;
			#endregion
		}
		#endregion
		
