			this.strSelect = @"SELECT vecode, vename1, vename2, vestrasse, veort, veland, veemail
INTO :frmVersicherungen.tblVe.colCode,
:frmVersicherungen.tblVe.colN1, :frmVersicherungen.tblVe.colN2, :frmVersicherungen.tblVe.colS, :frmVersicherungen.tblVe.colO, :frmVersicherungen.tblVe.colLand,
:frmVersicherungen.tblVe.colEMail
FROM ve ORDER BY vecode";
			Int.SqlConnection(ref this.hSqlIK);
			if (this.tblVe.Populate(this.hSqlIK, this.strSelect, Sys.TBL_FillAll)) 
