		/// <summary>
		/// SAM_FetchRowDone event handler.
		/// </summary>
		/// <param name="sender"></param>
		/// <param name="e"></param>
		private void tblHalter_OnSAM_FetchRowDone(object sender, WindowActionsEventArgs e)
		{
			#region Actions
			e.Handled = true;
			// 30.07.03
			if (this.colnHalterNein.Number == 1) 
			{
				XSal.TblSetRowBackColor(this, Sys.lParam, 0xbfbfff);
			}
			else
			{
				// 18.09.02
				if (this.bPFarbe) 
				{
					XSal.TblSetRowBackColor(this, Sys.lParam, 0xffbfbf);
					this.bPFarbe = false;
				}
				else
				{
					XSal.TblSetRowBackColor(this, Sys.lParam, 0xffefef);
					this.bPFarbe = true;
				}
			}
			#endregion
		}
		
