		/// <summary>
		/// SAM_Click event handler.
		/// </summary>
		/// <param name="sender"></param>
		/// <param name="e"></param>
		private void colnHalterNein_OnSAM_Click(object sender, WindowActionsEventArgs e)
		{
			#region Actions
			e.Handled = true;
			if (this.colnHalterNein.Number == 1) 
			{
				Sal.MessageBox("Sperre kann nur im Halterstamm aufgehoben werden.", "Achtung", Sys.MB_Ok);
			}
			else
			{
				this.strKuerzel = App.frmMain.strUser.Left(2);
				this.strGrund = "";
				while (this.strGrund == "") 
				{
					dlgText.ModalDialog(this, "Warum darf der Halter nicht mehr verwendet werden? :", "frmHalter", 100, ref this.strGrund);
				}
				this.lsGrund = "***** Halter gesperrt wg.: " + this.strGrund;
				// 13.05.14 F2027
				this.colnHalterNr.Number = Int.PalCheckNULL(this.colnHalterNr.Number);

				Int.SqlImmed(@"
INSERT INTO Z (zvon, zan, zarztnr, zhalternr, zrechnr, zbemerk, zdatum, zwiedervor, zerledigt, zart, zzm, zzmdm, zproznr, zan0, zan1, zan2, zan3, zan4, zanzeige, zerfdat)
VALUES (:strBNZei, '*', :tblHalter.colnArztNr, :tblHalter.colnHalterNr, 0,
:tblHalter.lsGrund , SYSDATE, NULL, 'J', 'HS', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL,0, SYSDATETIME)");
				this.strGrund = "";
				while (this.strGrund == "") 
				{
					dlgText.ModalDialog(this, "Andere Nummern des Halters (ohne Arzt-Nr.)? :", "frmHalter", 20, ref this.strGrund);
				}
				Int.SqlImmed("UPDATE h SET hhalternein=1, handerehalter = :tblHalter.strGrund WHERE harztnr = :tblHalter.colnArztNr and hhalternr = :tblHalter.colnHalterNr");
				this.colnHalterNein.Number = 1;
			}
			#endregion
		}
		
