		/// <param name="e"></param>
		private void tblUeberw_OnSAM_Create(object sender, WindowActionsEventArgs e)
		{
			#region Actions
			e.Handled = true;
			Sal.WaitCursor(true);
			Int.PalPrepareMTbl(this, 0);
			Int.SqlImmedSel(@"SELECT aname1, anr2
into :tblUeberw.strArztName, :tblUeberw.nArztNr2
from A where aarztnr=:tblUeberw.nArztNr");
			if (this.nArztNr2 == SalNumber.Null || this.nArztNr2 == 0) 
			{
				this.nArztNr2 = this.nArztNr;
				this.SetText("Überweisungen im lfd. Monat für " + Int.PalStrNum(this.nArztNr, 4, 0));
			}
			else
			{
				this.SetText("Überweisungen im lfd. Monat für " + Int.PalStrNum(this.nArztNr, 4, 0) + " und " + Int.PalStrNum(this.nArztNr2, 4, 0));
			}
			Int.SqlConnection(ref this.hSqlUeberw);
			// 23.11.18 +
			// ORDER BY tkarztnr, tkbeldat
			if (this.Populate(this.hSqlUeberw, @"SELECT tkbeldat, tkbuchtext, tksoll FROM tk
WHERE " + Int.PalArztNr("tk", this.nArztNr, this.nArztNr2) + @"  AND tksoll<>0 AND tkbuchtext LIKE '+%'
ORDER BY tkarztnr, tkbeldat", Sys.TBL_FillAll)) 
			{
				// Call VisTblAutoSizeColumn(tblUeberw, hWndNULL)
				MT.MTbl.AutoSizeColumn(this, Sys.hWndNULL, MT.MTbl.MTASC_ALLROWS);
			}
			Sal.WaitCursor(false);
			#endregion
		}
		
