order by zabuchdat", Sys.TBL_FillAllBackground);
			}
			else
			{
				this.Populate(Var.hSqlZa, @"select zabuchdat,zabelegnr,zadm,zakz, zabuchtext, zaproznr from za
into :tblZahlungen.coldtZADat,:tblZahlungen.colnZABelNr,
:tblZahlungen.colnZADM,:tblZahlungen.colnZAKZ, :tblZahlungen.colsZABuchtext, :tblZahlungen.colnZAProzNr
where " + Int.PalArztNr("za", this.nArztNr, this.nArztNr2) + @" and zahalternr=:tblZahlungen.nHalterNr AND zadeaktiv IS NULL
